import { LogLevels } from '@aztec/foundation/log';
import { schemas } from '@aztec/foundation/schemas';
import { z } from 'zod';
import { AztecAddress } from '../aztec-address/index.js';
/*
 * Represents a debug log emitted by public bytecode.
 */ export class DebugLog {
    contractAddress;
    level;
    message;
    fields;
    constructor(contractAddress, level, message, fields){
        this.contractAddress = contractAddress;
        this.level = level;
        this.message = message;
        this.fields = fields;
    }
    static get schema() {
        return z.object({
            contractAddress: AztecAddress.schema,
            level: z.enum(LogLevels),
            message: z.string(),
            fields: z.array(schemas.Fr)
        }).transform(({ contractAddress, level, message, fields })=>new DebugLog(contractAddress, level, message, fields));
    }
}
